import { Component } from '@angular/core';
import { Experiencia } from 'src/app/model/experiencia';
import { ExperienciaService } from 'src/app/services/experiencia.service';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-edit-experiencia',
  templateUrl: './edit-experiencia.component.html',
  styleUrls: ['./edit-experiencia.component.css'],
})
export class EditExperienciaComponent {
  expLab!: Experiencia;

  constructor(
    private sExperiencia: ExperienciaService,
    private activatedRoute: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    const id = this.activatedRoute.snapshot.params['id'];
    this.sExperiencia.detail(id).subscribe(
      (data) => {
        this.expLab = data;
      },
      (err) => {
        alert('error al modificar experiencia');
        this.router.navigate(['']);
      }
    );
  }

  onUpdate(): void {
    const id = this.activatedRoute.snapshot.params['id'];
    this.sExperiencia.update(id, this.expLab).subscribe(
      (data) => {
        this.router.navigate(['']);
      },
      (err) => {
        alert('Error al modificar experiencia');
        this.router.navigate(['']);
      }
    );
  }
}
<div class="container">
  <div class="row">
    <div>
      <form (ngSubmit)="onCreate()" novalidate #f="ngForm">
        <div class="form-group">
          <label for="nombreE">Nombre de la empresa</label>
          <input
            type="text"
            id="nombreE"
            class="form-control"
            name="nombreE"
            [(ngModel)]="nombreE"
            required
          />
        </div>
        <div class="form-group">
          <label for="nombreE">Descripcion de la tarea</label>
          <input
            type="text"
            id="descripcionE"
            class="form-control"
            name="descripcionE"
            [(ngModel)]="descripcionE"
            required
          />
        </div>
        <button class="btn btn-primary">Agregar experiencia</button>
      </form>
    </div>
  </div>
</div>
