import { Component } from '@angular/core';
import { persona } from 'src/app/model/persona.model';
import { PersonaService } from 'src/app/services/persona.service';

@Component({
  selector: 'app-acerca-de',
  templateUrl: './acerca-de.component.html',
  styleUrls: ['./acerca-de.component.css'],
})
export class AcercaDeComponent {
  persona: persona = new persona('Nicolas', 'Koch', 'UnaImagen');

  constructor(public personaService: PersonaService) {}

  ngOnInit(): void {
    this.personaService.getPersona().subscribe((data) => (this.persona = data));
  }
}
<div class="row">
  <div class="col-xs-12 col-sm-2 col-md-2 col-lg-2">
    <img
      id="foto-perfil"
      src="../../../assets/perfil-foto.jpg"
      class="img-fluid"
      alt=""
    />
  </div>
  <div class="col-xs-12 col-sm-2 col-md-2 col-lg-2">
    <h4 id="nombre">{{ persona.nombre }} {{ persona.apellido }}</h4>
    <p>Full Stack Web Developer Jr.</p>
  </div>
  <div class="col-xs-12 col-sm-8 col-md-8 col-lg-8">
    <span>
      <h2>Acerca de mi</h2>
      <p>
        Lorem ipsum dolor sit amet consectetur, adipisicing elit. Reiciendis
        ducimus dolorum animi molestias veritatis praesentium adipisci error quo
        inventore! Porro iusto consequuntur voluptas incidunt consequatur alias
        reprehenderit aspernatur facere molestias!
      </p>
    </span>
  </div>
</div>
