import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Educacion } from 'src/app/model/educacion';
import { EducacionService } from 'src/app/services/educacion.service';

@Component({
  selector: 'app-edit-educacion',
  templateUrl: './edit-educacion.component.html',
  styleUrls: ['./edit-educacion.component.css'],
})
export class EditEducacionComponent implements OnInit {
  educacion!: Educacion;

  constructor(
    private educacionService: EducacionService,
    private activatedRoute: ActivatedRoute,
    private router: Router
  ) {}

  ngOnInit(): void {
    const id = this.activatedRoute.snapshot.params['id'];
    this.educacionService
      .details(id)
      .subscribe((data) => (this.educacion = data));
  }

  onUpdate(): void {
    const id = this.activatedRoute.snapshot.params('id');
    this.educacionService.update(id, this.educacion).subscribe(
      (data) => this.router.navigate(['']),
      (err) => {
        alert('Error al modificar la educacion');
        this.router.navigate(['']);
      }
    );
  }
}
<div class="container">
  <div class="row">
    <div>
      <form (ngSubmit)="onUpdate()" novalidate #f="ngForm">
        <div class="form-group">
          <label for="nombreE">Nombre de la institucion</label>
          <input
            type="text"
            id="nombreE"
            class="form-control"
            name="nombreE"
            [(ngModel)]="nombreE"
            required
          />
        </div>
        <div class="form-group">
          <label for="nombreE">Descripcion de sus estudios</label>
          <input
            type="text"
            id="descripcionE"
            class="form-control"
            name="descripcionE"
            [(ngModel)]="descripcionE"
            required
          />
        </div>
        <button class="btn btn-primary">Agregar educacion</button>
      </form>
    </div>
  </div>
</div>
